	super.close();

	if (checkpointLock != null) {
		synchronized (checkpointLock) {
			globalModificationTime = Long.MAX_VALUE;
			isRunning = false;
		}
	}

	if (LOG.isDebugEnabled()) {
		LOG.debug("Closed File Monitoring Source for path: " + path + ".");
	}
}

